businessRules.rule:

  /* prevent the default action processing */
    set @BusinessRules_PreventDefault = 1
    /* set the focus on the field and display an error */
    set @Result_Focus = 'studentBalance1, @branch'

set @studentBalance1=(select sum(isnull(tr_db,0))-sum(isnull(tr_cr,0)) from  GLschstatment where branch=@branch and foryearcode=@AcademicYearto and GLschstatment.CSMSUP_NO=@StudentCode and GLschstatment.cstyp_NO=3)

update schApplication set studentBalance1=@studentBalance1 where AcademicYearto=@AcademicYearto and studentcode=@StudentCode and branch=@branch


if @studentBalance1>0
begin
    /* prevent the default action processing */
    set @BusinessRules_PreventDefault = 1
    /* set the focus on the field and display an error */
    set @Result_Focus = 'studentBalance1, More than zero .'
end
